
public class PrintNumberInWord {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 11;
		switch(number){
		case 1: 
			System.out.println("ONE");
			break;
		case 2:
			System.out.println("TWO");
			break;
			
		default: System.out.println("Number is invalid");
		
		}
	}

}
